mapper.select:
SELECT 

FROM car a

WHERE a.id = #{id}

SELECT 

FROM car a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.plate_number = #{plateNumber}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM car a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE car SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
